html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto; display:block;" alt="Full Page Image">
{% else %}
<style>
  @page {
    size: A4;
    margin: 5mm;
  }
  html, body {
    margin:0;
    padding:0;
    font-family: Arial, sans-serif;
    color:#000;
  }

  .page {
    width:100%;
    height:100%;
    text-align:center;
  }

  .id-card {
    display:inline-block;
    background:#ffffff;
    border:2px solid #1c3d7a;
    border-radius:14px;
    padding:14px 18px;
    width: 190mm;              /* card width */
    max-width:100%;
    box-sizing:border-box;
  }

  /* Header (logo + company name) */
  .id-header {
    display:flex;
    align-items:center;
    justify-content:space-between;
    border-bottom:1px solid #ddd;
    padding-bottom:6px;
    margin-bottom:8px;
  }

  .id-header-left {
    display:flex;
    align-items:center;
    gap:8px;
  }

  .id-header-logo {
    width:22mm;
    height:22mm;
    border-radius:4px;
    overflow:hidden;
    display:flex;
    align-items:center;
    justify-content:center;
  }

  .id-header-logo img {
    width:100%;
    height:100%;
    object-fit:contain;
  }

  .id-header-title {
    text-align:left;
  }

  .id-header-title h2 {
    margin:0;
    font-size:16px;
    font-weight:bold;
    color:#114ad6;
  }
  .id-header-title .sub {
    font-size:9px;
    color:#444;
  }

  .id-header-right {
    width:22mm;
    height:22mm;
    display:flex;
    align-items:center;
    justify-content:flex-end;
  }

  .id-header-right .course-logo {
    width:22mm;
    height:22mm;
    background-size:contain;
    background-repeat:no-repeat;
    background-position:center;
  }

  /* Body layout */
  .id-body {
    display:flex;
    justify-content:space-between;
    gap:10px;
    margin-top:4px;
  }

  .id-left,
  .id-center,
  .id-right {
    box-sizing:border-box;
  }

  .id-left {
    width:22%;
    display:flex;
    align-items:flex-start;
    justify-content:center;
  }

  .photo-box {
    width:32mm;
    height:42mm;
    border:2px solid #114ad6;
    display:flex;
    align-items:center;
    justify-content:center;
    font-size:9px;
    color:#888;
  }
  .photo-box img {
    width:100%;
    height:100%;
    object-fit:cover;
  }

  .id-center {
    width:50%;
    text-align:left;
  }
  .id-center-name {
    font-size:14px;
    font-weight:bold;
    margin-bottom:4px;
  }
  .id-center-course {
    font-size:11px;
    font-weight:bold;
    margin-bottom:4px;
  }
  .id-center-ref {
    font-size:8px;
    margin-bottom:6px;
  }
  .id-center-desc {
    font-size:9px;
    line-height:1.3;
    margin-bottom:6px;
  }
  .id-center-meta p {
    margin:2px 0;
    font-size:9px;
  }
  .label {
    font-weight:bold;
  }

  .id-right {
    width:22%;
    display:flex;
    align-items:flex-start;
    justify-content:flex-end;
  }
  .qr-box img {
    width:32mm;
    height:32mm;
  }

  /* Signatures row */
  .id-signatures {
    display:flex;
    justify-content:space-between;
    margin-top:8px;
    font-size:9px;
  }
  .sig-block {
    width:32%;
    text-align:center;
  }
  .sig-block img {
    width:32mm;
    height:18mm;
    object-fit:contain;
    display:block;
    margin:0 auto 2px auto;
  }
  .sig-title {
    border-top:1px solid #333;
    padding-top:2px;
    margin-top:2px;
  }

  /* Footer logos strip */
  .id-footer {
    margin-top:6px;
  }
  .id-footer img {
    width:100%;
    height:auto;
    display:block;
  }

  img, td, table {
    border:0 !important;
    outline:none !important;
  }
</style>

<div class="page">

  {# Course & logos #}
  {% set course = frappe.get_doc("Course", doc.course) %}
  {% set left_logo_path = "/files/Screenshot from 2025-07-15 08-45-36.png" %}
  {% set left_logo_url  = frappe.utils.get_url(left_logo_path) %}

  {% if course.logo and '/private/' not in course.logo %}
    {% set right_logo_url = frappe.utils.get_url(course.logo) %}
  {% else %}
    {% set right_logo_url = None %}
  {% endif %}

  {% set dt = frappe.utils.get_datetime(doc.custom_certificate_validity_date) if doc.custom_certificate_validity_date else None %}
  {% if dt %}
    {% set day = dt.day %}
    {% set suffix = (
        'th' if 11 <= day <= 13 else
        'st' if day % 10 == 1 else
        'nd' if day % 10 == 2 else
        'rd' if day % 10 == 3 else
        'th'
    ) %}
  {% endif %}

  {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ (doc.name or "") %}

  <div class="id-card">

    <!-- HEADER -->
    <div class="id-header">
      <div class="id-header-left">
        <div class="id-header-logo">
          <img src="{{ left_logo_url }}" alt="Left Logo">
        </div>
        <div class="id-header-title">
          <h2>NUMERO UNO TRAINING &amp; CONSULTING LLC</h2>
          {% if course.custom_certificate_type %}
            <div class="sub">
              CERTIFICATE OF {{ (course.custom_certificate_type or '').upper() }}
            </div>
          {% endif %}
        </div>
      </div>

      <div class="id-header-right">
        {% if right_logo_url %}
          <div class="course-logo" style="background-image:url('{{ right_logo_url }}');"></div>
        {% endif %}
      </div>
    </div>

    <!-- BODY -->
    <div class="id-body">
{% set student_photo = frappe.db.get_value("Student", doc.student, "custom_photo") %}

      <!-- LEFT: PHOTO -->
     <div class="photo-box">
  {% if student_photo %}
    <img src="{{ frappe.utils.get_url(student_photo) }}" alt="Student Photo">
  {% else %}
    Photo
  {% endif %}
</div>



      <!-- CENTER: TEXT -->
      <div class="id-center">
        <div class="id-center-name">
          {{ doc.student_name }}
        </div>

        <div class="id-center-course">
          {% if doc.course %}
            {{ doc.course }}
          {% endif %}
        </div>

        {% if dt %}
          <div class="id-center-ref">
            Awarded on {{ day }}{{ suffix }} {{ dt.strftime("%B %Y") }}
          </div>
        {% endif %}

        {% if course.description %}
        <div class="id-center-desc">
          {{ course.description }}
        </div>
        {% endif %}

        <div class="id-center-meta">
          <p><span class="label">Course Date:</span>
            {{ frappe.utils.get_datetime(doc.course_start_date).strftime("%d-%m-%Y") }}
            to
            {{ frappe.utils.get_datetime(doc.course_end_date).strftime("%d-%m-%Y") }}
          </p>

          <p><span class="label">Expiry Date:</span>
            {% if doc.validity_period and doc.validity_period != 'NA' and doc.certificate_validity_date %}
              {{ frappe.utils.get_datetime(doc.certificate_validity_date).strftime("%d-%m-%Y") }}
            {% else %}
              N/A
            {% endif %}
          </p>

          <p><span class="label">Certificate No:</span> {{ doc.name }}</p>
        </div>
      </div>

      <!-- RIGHT: QR -->
      <div class="id-right">
        <div class="qr-box">
          <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150"
               alt="QR Code">
        </div>
      </div>

    </div>

    <!-- SIGNATURES -->
    <div class="id-signatures">
      <div class="sig-block">
        {# Instructor signature #}
        {% set instructor_link = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
        {% if instructor_link %}
          {% set instructor_doc = frappe.get_doc("Instructor", instructor_link) %}
          {% if instructor_doc.image %}
            <img src="{{ frappe.utils.get_url(instructor_doc.image) }}" alt="Instructor Signature">
          {% endif %}
          <div class="sig-title">{{ instructor_doc.instructor_name }}<br>Instructor</div>
        {% else %}
          <div class="sig-title">Instructor</div>
        {% endif %}
      </div>

      <div class="sig-block">
        <!-- Empty center (if you want extra text you can place it here) -->
      </div>

      <div class="sig-block">
        <img src="/files/samy sign.png" alt="Director Signature">
        <div class="sig-title">Academic Director</div>
      </div>
    </div>

    <!-- FOOTER LOGOS STRIP -->
    <div class="id-footer">
      <img src="/files/FOOTER.png" alt="Footer Logos">
    </div>

  </div>  <!-- /.id-card -->
</div>      <!-- /.page -->
{% endif %}
