html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto; display:block;" alt="Full Page Image">
{% else %}
<style>
  @page {
    size: A4;
    margin: 5mm;
  }
  html, body {
    margin:0;
    padding:0;
    font-family: Arial, sans-serif;
    color:#000;
  }

  .page {
    width:100%;
    height:100%;
    text-align:center;
  }

  .id-card {
    display:inline-block;
    background:#ffffff;
    border:2px solid #1c3d7a;
    border-radius:14px;
    padding:6px 12px;
    width: 190mm;
    max-width:100%;
    box-sizing:border-box;
  }

  /* Header (logo + company name) */
  .id-header {
    display:flex;
    align-items:center;
    justify-content:center;
    border-bottom:1px solid #ddd;
    padding:2px 0;
    margin-bottom:4px;
    text-align:center;
  }

  .id-header-left {
    display:flex;
    align-items:center;
    justify-content:center;
    gap:8px;
  }

  .id-header-logo {
    width:18mm;
    height:18mm;
    border-radius:4px;
    overflow:hidden;
    display:flex;
    align-items:center;
    justify-content:center;
  }

  .id-header-logo img {
    width:100%;
    height:100%;
    object-fit:contain;
  }

  .id-header-title h2 {
    margin: 0;
    font-size: 22px;      /* Bigger company name */
    font-weight: 900;     /* Extra bold */
    letter-spacing: 0.5px;
    color: #114ad6;
    text-transform: uppercase;

  }

  /* Body layout */
  .id-body {
    display:flex;
    justify-content:space-between;
    gap:10px;
    margin-top:4px;
  }

  .id-left,
  .id-center,
  .id-right {
    box-sizing:border-box;
  }

  .id-left {
    width:22%;
    display:flex;
    align-items:flex-start;
    justify-content:center;
  }

  .photo-box {
    width:32mm;
    height:42mm;
    border:2px solid #114ad6;
    display:flex;
    align-items:center;
    justify-content:center;
    font-size:9px;
    color:#888;
    margin-top:28px;
  }
  .photo-box img {
    width:100%;
    height:100%;
    object-fit:cover;
  }

  .id-center {
    width:50%;
    text-align:center;
  }
  .id-center-name {
    font-size:17px;
    font-weight:bold;
    margin-bottom:4px;
    margin-top:36px;
  }
  .id-center-course {
    font-size:14px;
    font-weight:bold;
    margin-bottom:4px;
  }
  .id-center-ref {
    font-size:11px;
    margin-bottom:6px;
  }
  .id-center-desc {
    font-size:9px;
    line-height:1.3;
    margin-bottom:6px;
  }
  .id-center-meta p {
    margin:2px 0;
    font-size:13px;
    text-align:center;
  }
  .label {
    font-weight:bold;
  }

  .id-right {
    width:22%;
    display:flex;
    align-items:flex-start;
    justify-content:flex-end;
    padding-right:6mm;
  }
  .qr-box {
    margin-top:10px;
    margin-right:30px;
  }
  .qr-box img {
    width:30mm;
    height:30mm;
  }

  /* Signatures row */
  .id-signatures {
    display:flex;
    justify-content:flex-start;
    margin-top:8px;
    font-size:9px;
  }
  .sig-block {
    width:32%;
    text-align:center;
  }
  .sig-block img {
    width:32mm;
    height:18mm;
    object-fit:contain;
    display:block;
    margin:0 auto 2px auto;
  }
  .sig-title {
    border-top:1px solid #333;
    padding-top:2px;
    margin-top:2px;
  }

  /* Footer logos strip */
  .id-footer {
    margin-top:6px;
  }
  .id-footer img {
    width:100%;
    height:auto;
    display:block;
  }

  img, td, table {
    border:0 !important;
    outline:none !important;
  }
</style>

<div class="page">

  {# Course & logos #}
  {% set course = frappe.get_doc("Course", doc.course) %}
  {% set left_logo_path = "/files/Screenshot from 2025-07-15 08-45-36.png" %}
  {% set left_logo_url  = frappe.utils.get_url(left_logo_path) %}

  {% set dt = frappe.utils.get_datetime(doc.custom_certificate_validity_date) if doc.custom_certificate_validity_date else None %}
  {% if dt %}
    {% set day = dt.day %}
    {% set suffix = (
        'th' if 11 <= day <= 13 else
        'st' if day % 10 == 1 else
        'nd' if day % 10 == 2 else
        'rd' if day % 10 == 3 else
        'th'
    ) %}
  {% endif %}

  {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ (doc.name or "") %}
  {% set student_photo = frappe.db.get_value("Student", doc.student, "custom_photo") %}

  {% set customer_name = frappe.db.get_value("Student", doc.student, "customer_name") %}

  <div class="id-card">

    <!-- HEADER -->
    <div class="id-header">
      <div class="id-header-left">
        <div class="id-header-logo">
          <img src="{{ left_logo_url }}" alt="Left Logo">
        </div>
        <div class="id-header-title">
          <h2>NUMERO UNO TRAINING &amp; CONSULTING LLC</h2>
        </div>
      </div>
    </div>

    <!-- BODY -->
    <div class="id-body">

      {# LEFT: PHOTO – only if available #}
      {% if student_photo %}
      <div class="id-left">
        <div class="photo-box">
          <img src="{{ frappe.utils.get_url(student_photo) }}" alt="Student Photo">
        </div>
      </div>
      {% endif %}

      <!-- CENTER: TEXT -->
      <div class="id-center"
           style="{% if not student_photo %}width:72%; margin-left:14%;{% endif %}">
        <div class="id-center-name">
          {{ doc.student_name }}
        </div>

        <div class="id-center-course">
          {% if doc.course %}
            {{ doc.course }}
          {% endif %}
        </div>

        {% if dt %}
          <div class="id-center-ref">
            Awarded on {{ day }}{{ suffix }} {{ dt.strftime("%B %Y") }}
          </div>
        {% endif %}

        {% if course.description %}
        <div class="id-center-desc">
          {{ course.description }}
        </div>
        {% endif %}

        <div class="id-center-meta">
          <p><span><strong>Course Date:</strong></span>
            {{ frappe.utils.get_datetime(doc.course_start_date).strftime("%d-%m-%Y") }}
            to
            {{ frappe.utils.get_datetime(doc.course_end_date).strftime("%d-%m-%Y") }}
          </p>

          <p><span ><strong>Expiry Date:</strong></span>
            {% if doc.validity_period and doc.validity_period != 'NA' and doc.certificate_validity_date %}
              {{ frappe.utils.get_datetime(doc.certificate_validity_date).strftime("%d-%m-%Y") }}
            {% else %}
              N/A
            {% endif %}
          </p>

          <p><span ><strong>Certificate No:</strong></span> {{ doc.name }}</p>

                    <p><span ><strong>Customer:</strong></span> {{ customer_name }}</p>

        </div>
      </div>

      <!-- RIGHT: QR -->
      <div class="id-right">
        <div class="qr-box">

        </div>
      </div>

    </div>

<!-- SIGNATURES -->
<div class="id-signatures"
     style="display:flex; justify-content:space-between; align-items:flex-end; margin-top:15px;">

    <!-- Instructor Block -->
    <div class="sig-block"
         style="text-align:center; width:40%; display:flex; flex-direction:column; justify-content:flex-end; height:32mm;">

        {% set instructor_link = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
        {% if instructor_link %}
            {% set instructor_doc = frappe.get_doc("Instructor", instructor_link) %}

            {% if instructor_doc.image %}
                <img src="{{ frappe.utils.get_url(instructor_doc.image) }}"
                     alt="Instructor Signature"
                     style="width:32mm; height:18mm; object-fit:contain; margin:auto;">
            {% endif %}

            <div class="sig-title"
                 style="border-top:1px solid #333; margin-top:6px; padding-top:3px;">
                {{ instructor_doc.instructor_name }}<br>Instructor
            </div>

        {% else %}
            <div class="sig-title"
                 style="border-top:1px solid #333; margin-top:6px; padding-top:3px;">
                Instructor
            </div>
        {% endif %}
    </div>

    <!-- QR Code on Right Side -->
    <div class="qr-box"
     style="text-align:right; width:40%; padding-bottom:2px;">
    <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150"
         alt="QR Code"
         style="width:30mm; height:30mm; transform: translateY(-30px);">
</div>


</div>



    <!-- FOOTER LOGOS STRIP -->
    <div class="id-footer">
      <img src="/files/FOOTER.png" alt="Footer Logos">
    </div>

  </div>  <!-- /.id-card -->
</div>      <!-- /.page -->
{% endif %}
