html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto; display:block;" alt="Full Page Image">
{% else %}
<style>
  @page { size: A4; margin: 5mm 5mm 5mm 5mm; }
  html, body { margin:0; padding:0; font-family: Arial, sans-serif; color:#000; }

  .container { width:100%; text-align:center; }

  .logo { text-align:left; margin-bottom:20px; }
  .logo img { width:150px; height:150px; object-fit:contain; }

  h1, h2, h3, h4, h5 { margin:4px 0; }
  h1 { font-size:22px; }
  h2 { font-size:28px; color:#114ad6; }

  .student { margin:12px 0; font-size:22px;  }
  .desc { margin:10px auto; width:80%; }

  table.details { width:60%; margin:20px auto; font-size:12px; border-collapse:collapse; }
  table.details td { padding:3px 5px; vertical-align:top; }

  .signatures { width:100%; margin-top:30px; border-collapse:collapse; }
  .signatures td { width:33%; text-align:center; vertical-align:bottom; }

  .footer-img { width:100%; margin-top:20px; }
</style>

<div class="container">
{% set course = frappe.get_doc("Course", doc.course) %}

<!-- Logos row (PDF-safe) -->
<table style="width:100%; border-collapse:collapse; margin-bottom:16px;">
  <tr>
    <!-- Left fixed logo -->
    <td style="text-align:left; vertical-align:top; width:140px;">
      <img
        src="/files/Screenshot from 2025-07-15 08-45-36.png"
        alt="Left Logo"
        style="height:150px; width:auto; display:block; object-fit:contain;">
    </td>

    <!-- Spacer -->
    <td></td>

    <!-- Right dynamic logo from Course.logo (with fallback) -->
    <td style="text-align:right; vertical-align:top; width:140px;">
      {% if course.logo %}
        <img
          src="{{ course.logo }}"
          alt="Right Logo"
          style="height:150px; width:auto; display:block; object-fit:contain;">
      {% else %}
        <img
          src="/files/default-course-logo.png"
          alt="Default Logo"
          style="height:150px; width:auto; display:block; object-fit:contain;">
      {% endif %}
    </td>
  </tr>
</table>

<br/>

  <!-- Header -->
  {% set course = frappe.get_doc("Course", doc.course) %}
  <h1><b>CERTIFICATE OF {{ (course.custom_certificate_type or '').upper() }}</b></h1>
  <br/>
  <h2>NUMERO UNO TRAINING &amp; <br/> CONSULTING LLC</h2>

    <br/>
    <br/>

  <!-- Body -->
 <strong> <h4>This is to certify that</h4></strong>

 <br/>
  <div class="student">{{ doc.student_name }}</div>

  <br/>

  <p style="font-size:16px">has successfully Attended / Passed</p>

  <br/>

  {% if doc.course %}
   <strong> <p style="font-size:16px">{{ doc.course }}</p></strong>
  {% endif %}

  <div class="desc">{{ course.description or "" }}</div>


<br/><br/><br/>

{% set dt = frappe.utils.get_datetime(doc.custom_certificate_validity_date) %}
{% if dt %}
  {% set day = dt.day %}
  {% set suffix = (
      'th' if 11 <= day <= 13 else
      'st' if day % 10 == 1 else
      'nd' if day % 10 == 2 else
      'rd' if day % 10 == 3 else
      'th'
  ) %}
  <p style="font-style:italic; text-align:left; margin-left:30px;">
    Awarded on {{ day }}{{ suffix }} {{ dt.strftime("%B %Y") }}
  </p>
{% endif %}

<div style="text-align:left; margin-left:150px; font-size:16px; line-height:1.6;">

  <p>
    <strong>Course Date:</strong>
    {{ frappe.utils.get_datetime(doc.course_start_date).strftime("%d-%m-%Y") }}
    to
    {{ frappe.utils.get_datetime(doc.course_end_date).strftime("%d-%m-%Y") }}
  </p>

  <p>
    <strong>Expiry Date:</strong>
    {% if doc.validity_period and doc.validity_period != 'NA' and doc.certificate_validity_date %}
      {{ frappe.utils.get_datetime(doc.certificate_validity_date).strftime("%d-%m-%Y") }}
    {% else %}
      N/A
    {% endif %}
  </p>

  <p>
    <strong>Certificate No:</strong> {{ doc.name }}
  </p>

</div>


<!--<br/>-->
<!--<br/>-->
<!--<br/>-->
<!--<br/>-->

  <!-- Signatures -->
  <table class="signatures">
    <tr>
      <td>
        {% set instructor_name = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor_name") %}
        {% if instructor_name %}
          <img src="/files/Screenshot from 2025-07-15 08-49-17.png" width="120" height="70" alt="Instructor Signature"><br>
          {{ instructor_name }}<br>Instructor
        {% else %}
          Instructor
        {% endif %}
      </td>

      <td>
        {% set base = frappe.utils.get_url() %}
        {% set verify_url = base ~ "/certificate-verification?cert=" ~ (doc.name or "") %}
        <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150"
             width="120" height="120" alt="QR Code">
      </td>

      <td>
        <img src="/files/samy sign.png" width="120" height="70" alt="Director Signature"><br>
        Academic Director
      </td>
    </tr>
  </table>

    <br/>
  <!-- Footer -->
  <img src="/files/FOOTER.png" class="footer-img" alt="Footer">

</div>
{% endif %}
