html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto; display:block;" alt="Full Page Image">
{% else %}
<style>
  @page { size: A4; margin: 5mm; }
  html, body { margin:0; padding:0; font-family: Arial, sans-serif; color:#000; }
  .container { width:100%; text-align:center; }

  h1, h2, h3, h4, h5 { margin:4px 0; }
  h1 { font-size:22px; }
  h2 { font-size:28px; color:#114ad6; }

  .student { margin:12px 0; font-size:22px; }
  .desc { margin:10px auto; width:80%; }

  .signatures { width:100%; margin-top:30px; border-collapse:collapse; }
  .signatures td { width:33%; text-align:center; vertical-align:bottom; }

  .footer-img { width:100%; margin-top:10px; }

  img, td, table { border:0 !important; outline:none !important; }
</style>

<div class="container">

{% set course = frappe.get_doc("Course", doc.course) %}
{% set left_logo_path = "/files/Screenshot from 2025-07-15 08-45-36.png" %}
{% set left_logo_url  = frappe.utils.get_url(left_logo_path) %}

{# right logo only if available and public #}
{% if course.logo and '/private/' not in course.logo %}
  {% set right_logo_url = frappe.utils.get_url(course.logo) %}
{% else %}
  {% set right_logo_url = None %}
{% endif %}

<!-- Logo Row -->
<table style="width:100%; border-collapse:collapse; margin-bottom:16px;">
  <tr>
    <!-- Left Fixed Logo -->
    <td style="text-align:left; vertical-align:top; width:140px;">
      <img src="{{ left_logo_url }}" alt="Left Logo"
           style="height:150px; width:auto; display:block; object-fit:contain;">
    </td>

    <td></td>

    <!-- Right Course Logo (show only if exists) -->
    {% if right_logo_url %}
    <td style="text-align:right; vertical-align:top; width:140px;">
      <div style="
        width:140px; height:150px;
        background-image:url('{{ right_logo_url }}');
        background-repeat:no-repeat;
        background-position:center;
        background-size:contain;">
      </div>
    </td>
    {% else %}
    <td style="width:140px; height:150px;"></td>
    {% endif %}
  </tr>
</table>

<!-- Header -->
<h1><b>CERTIFICATE OF {{ (course.custom_certificate_type or '').upper() }}</b></h1>
<br/>
<h2>NUMERO UNO TRAINING &amp; CONSULTING LLC</h2>

<br/>
<!--<br/>-->
<strong><h4>This is to certify that</h4></strong>
<br/>
<div class="student">{{ doc.student_name }}</div>

<br/>
<p style="font-size:16px">
  has successfully Completed

</p>
<!--<br/>-->

{% if doc.course %}
  <strong><p style="font-size:16px">{{ doc.course }} 
  {% if doc.custom_level %}
  {{doc.custom_level}}

  {% else %}

  {% endif %}

  </p></strong>
{% endif %}

<div class="desc">{{ course.description or "" }}</div>


{% set dt = frappe.utils.get_datetime(doc.custom_certificate_validity_date) %}
{% if dt %}
  {% set day = dt.day %}
  {% set suffix = (
      'th' if 11 <= day <= 13 else
      'st' if day % 10 == 1 else
      'nd' if day % 10 == 2 else
      'rd' if day % 10 == 3 else
      'th'
  ) %}
  <p style="font-style:italic; text-align:left; margin-left:30px;">
    Awarded on {{ day }}{{ suffix }} {{ dt.strftime("%B %Y") }}
  </p>
{% endif %}

<div style="text-align:left; margin-left:150px; font-size:16px; line-height:1.6;">
  <p><strong>Course Date:</strong>
    {{ frappe.utils.get_datetime(doc.course_start_date).strftime("%d-%m-%Y") }} to
    {{ frappe.utils.get_datetime(doc.course_end_date).strftime("%d-%m-%Y") }}
  </p>

  <p><strong>Expiry Date:</strong>
    {% if doc.validity_period and doc.validity_period != 'NA' and doc.certificate_validity_date %}
      {{ frappe.utils.get_datetime(doc.certificate_validity_date).strftime("%d-%m-%Y") }}
    {% else %}
      N/A
    {% endif %}
  </p>

  <p><strong>Certificate No:</strong> {{ doc.name }}</p>
</div>

<!-- Signatures -->
<table class="signatures">
  <tr>
   <td>
  {% set instructor_link = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
  {% if instructor_link %}
    {% set instructor_doc = frappe.get_doc("Instructor", instructor_link) %}
    {% if instructor_doc.image %}
      <img src="{{ frappe.utils.get_url(instructor_doc.image) }}" width="120" height="70" alt="Instructor Signature"><br>
    {% endif %}
    {{ instructor_doc.instructor_name }}<br>Instructor
  {% else %}
    Instructor
  {% endif %}
</td>



    <td>
      {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ (doc.name or "") %}
      <img src="{{ verify_url | qr_code }}" alt="QR Code">
      <!--<img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150"-->
      <!--     width="120" height="120" alt="QR Code">-->

           ACTVET License No.
<br/>0716/2013
    </td>

    <td>
      <img src="/files/samy sign.png" width="120" height="70" alt="Director Signature"><br>
      Academic Director
    </td>
  </tr>
</table>

<br/>
<img src="/files/FOOTER.png" class="footer-img" alt="Footer">
</div>
{% endif %}
