html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto; display:block;" alt="Full Page Image">
{% else %}
<style>
  @page { size: A4; margin: 5mm; }
  html, body { margin:0; padding:0; font-family: Arial, sans-serif; color:#000; }
  .container { width:100%; text-align:center; }

  h1, h2, h3, h4, h5 { margin:4px 0; }
  h1 { font-size:22px; }
  h2 { font-size:28px; color:#114ad6; }

  .student { margin:12px 0; font-size:22px; }
  .desc { margin:10px auto; width:80%; }

  .signatures { width:100%; margin-top:30px; border-collapse:collapse; }
  .signatures td { width:33%; text-align:center; vertical-align:bottom; }

  .footer-img { width:100%; margin-top:10px; }

  img, td, table { border:0 !important; outline:none !important; }
</style>

<div class="container">

{# ================= BASE URL (FIXED) ================= #}
{% set base_url = "https://erp.nutc.cloud" %}

{% set course = frappe.get_doc("Course", doc.course) %}

{# ================= LEFT LOGO ================= #}
{% set left_logo = (base_url ~ "/files/Screenshot from 2025-07-15 08-45-36.png")|replace(" ", "%20") %}

{# ================= RIGHT COURSE LOGO ================= #}
{% if course.logo and '/private/' not in course.logo %}
  {% if course.logo.startswith("http") %}
    {% set right_logo = course.logo %}
  {% else %}
    {% set right_logo = base_url ~ course.logo %}
  {% endif %}
  {% set right_logo = right_logo|replace(" ", "%20") %}
{% else %}
  {% set right_logo = None %}
{% endif %}

<!-- ================= LOGO ROW ================= -->
<table style="width:100%; border-collapse:collapse; margin-bottom:16px;">
  <tr>
    <td style="text-align:left; width:140px;">
      <img src="{{ left_logo }}" style="height:150px; object-fit:contain;">
    </td>
    <td></td>
    <td style="text-align:right; width:140px;">
      {% if right_logo %}
        <img src="{{ right_logo }}" style="height:150px; object-fit:contain;">
      {% endif %}
    </td>
  </tr>
</table>

<!-- ================= HEADER ================= -->
<h1><b>CERTIFICATE OF {{ (course.custom_certificate_type or '').upper() }}</b></h1>
<br/>
<h2>NUMERO UNO TRAINING &amp; CONSULTING LLC</h2>

<br/>
<strong><h4>This is to certify that</h4></strong>
<br/>
<div class="student">{{ doc.student_name }}</div>

<p style="font-size:16px">has successfully completed</p>

{% if doc.course %}
  <strong>
    <p style="font-size:16px">
      {{ doc.course }}{% if doc.custom_level %} {{ doc.custom_level }}{% endif %}
    </p>
  </strong>
{% endif %}

<div class="desc">{{ course.description or "" }}</div>

<!-- ================= AWARD DATE ================= -->
{% set dt = frappe.utils.get_datetime(doc.custom_certificate_validity_date) %}
{% if dt %}
  {% set d = dt.day %}
  {% set s = 'th' if 11 <= d <= 13 else 'st' if d % 10 == 1 else 'nd' if d % 10 == 2 else 'rd' if d % 10 == 3 else 'th' %}
  <p style="font-style:italic; text-align:left; margin-left:30px;">
    Awarded on {{ d }}{{ s }} {{ dt.strftime("%B %Y") }}
  </p>
{% endif %}

<!-- ================= DETAILS ================= -->
<div style="text-align:left; margin-left:150px; font-size:16px; line-height:1.6;">
  <p><strong>Course Date:</strong>
    {{ frappe.utils.get_datetime(doc.course_start_date).strftime("%d-%m-%Y") }} to
    {{ frappe.utils.get_datetime(doc.course_end_date).strftime("%d-%m-%Y") }}
  </p>
  <p><strong>Expiry Date:</strong>
    {% if doc.validity_period and doc.validity_period != 'NA' and doc.certificate_validity_date %}
      {{ frappe.utils.get_datetime(doc.certificate_validity_date).strftime("%d-%m-%Y") }}
    {% else %}N/A{% endif %}
  </p>
  <p><strong>Certificate No:</strong> {{ doc.name }}</p>
</div>

<!-- ================= SIGNATURES ================= -->
<table class="signatures">
  <tr>
    <!-- Instructor -->
    <td>
      {% set instructor = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
      {% if instructor %}
        {% set ins = frappe.get_doc("Instructor", instructor) %}
        {% if ins.image %}
          <img src="{{ (base_url ~ ins.image)|replace(' ', '%20') }}"
               style="width:120px; height:70px; object-fit:contain;"><br>
        {% endif %}
        {{ ins.instructor_name }}<br>Instructor
      {% else %}
        Instructor
      {% endif %}



    </td>

    <!-- QR -->
    <td>
      {% set verify_url = base_url ~ "/certificate-verification?cert=" ~ doc.name %}
      <img src="{{ verify_url | qr_code }}"><br>
      ACTVET License No.<br/>0716/2013
    </td>

    <!-- Director -->
    <td>
      <img src="{{ (base_url ~ '/files/samy sign.png')|replace(' ', '%20') }}"
           style="width:120px; height:70px; object-fit:contain;"><br>
      Academic Director
    </td>
  </tr>
</table>

<br/>

<!-- ================= FOOTER ================= -->
<img src="{{ (base_url ~ '/files/FOOTER.png')|replace(' ', '%20') }}" class="footer-img">

</div>
{% endif %}
