css:
.col-1{
      width: 50%; float: left; font-weight: bold;
      }
.col-2{
width: 50%; float: right; font-weight: bold;}	
p{ font-size: 12px; }

table, th, td {
  border: 1px solid black;
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header -%}

<div class="row">
  <div class="col-md-12" style="display:flex; width:100%; margin-top:-20px; margin-bottom:-20px;">

    {# Repeatable header wrapper (balanced tags) #}
    {% if print_settings.repeat_header_footer %}
      <div id="header-html" class="hidden-pdf">
        {% set letter_head = frappe.get_doc("Letter Head", "Letter Head New") %}
        {% if letter_head %}
          <div class="letter-head">{{ letter_head.content }}</div>
          <div class="row" style="text-align:center; margin-top:0;">
            <h2><b><u>ATTENDENCE SHEET</u></b></h2>
          </div>
          <div style="margin-top:2%; display:flex; gap:24px;">
            <div class="col-1">
              <p><b>Sheet No :</b> {{ doc.name }}</p>
              <p><b>Date :</b> {{ frappe.utils.formatdate(doc.creation) }}</p>
              <p><b>Course Start Date :</b> {{ frappe.utils.formatdate(doc.from_date) }}</p>
              <p><b>Course End Date :</b> {{ frappe.utils.formatdate(doc.to_date) }}</p>
            </div>
            <div class="col-2">
              <p><b>Confirmation Type :</b> {{ doc.custom_confirmation_type }}</p>
              <p><b>Training :</b> {{ doc.course }}</p>
              <p><b>Course Location :</b> {{ doc.custom_course_location_name }}</p>
            </div>
          </div>
        {% endif %}
      </div>
    {% else %}
      {# Non-repeating header content #}
      {% set letter_head = frappe.get_doc("Letter Head", "Letter Head New") %}
      {% if letter_head %}
        <div class="letter-head">{{ letter_head.content }}</div>
        <div class="row" style="text-align:center; margin-top:0;">
          <h2><b><u>ATTENDENCE SHEET</u></b></h2>
        </div>
        <div style="margin-top:2%; display:flex; gap:24px;">
          <div class="col-1">
            <p><b>Sheet No :</b> {{ doc.name }}</p>
            <p><b>Date :</b> {{ frappe.utils.formatdate(doc.creation) }}</p>
            <p><b>Course Start Date :</b> {{ frappe.utils.formatdate(doc.from_date) }}</p>
            <p><b>Course End Date :</b> {{ frappe.utils.formatdate(doc.to_date) }}</p>
          </div>
          <div class="col-2">
            <p><b>Confirmation Type :</b> {{ doc.custom_confirmation_type }}</p>
            <p><b>Training :</b> {{ doc.course }}</p>
            <p><b>Course Location :</b> {{ doc.custom_course_location_name }}</p>
          </div>
        </div>
      {% endif %}
    {% endif %}

  </div>
</div>

{# ---------------- MAIN CONTENT ---------------- #}
<div>
  {% if doc.students %}

    {# -------- Optional customer filter -------- #}
    {# Use either doc.custom_customer (preferred) or doc.customer if present #}
    {% set selected_customer = doc.custom_customer or doc.customer %}

    {% set customers = [] %}
    {% if selected_customer %}
      {# Filtered mode: only this customer #}
      {% set customers = [selected_customer] %}
    {% else %}
      {# No filter: collect all unique customer names from child rows #}
      {% for i in doc.students %}
        {% if i.customer_name and i.customer_name not in customers %}
          {% set _ = customers.append(i.customer_name) %}
        {% endif %}
      {% endfor %}
    {% endif %}

    {# -------- Build real date range once -------- #}
    {%- set start = frappe.utils.getdate(doc.custom_from_date or doc.from_date) -%}
    {%- set end = frappe.utils.getdate(doc.custom_to_date or doc.to_date) -%}
    {%- set total_days = frappe.utils.date_diff(end, start) + 1 -%}
    {%- set dates = [] -%}
    {%- for i in range(total_days) -%}
      {%- set dt = frappe.utils.add_days(start, i) -%}
      {%- set _ = dates.append(dt) -%}
    {%- endfor -%}

    {# -------- Loop per customer and render their table -------- #}
    {% for customer in customers %}
      {% set title = customer if customer else "&nbsp;" %}

      <div style="page-break-before: always;">
        <h3 style="float:left; margin-top:12%">
          {{ title }}
          {% if selected_customer %}<small style="font-weight:normal;"></small>{% endif %}
        </h3>

        <table width="100%" style="border:1px solid #000; border-collapse:collapse; margin-top:5%;">
          <tr>
            <th style="border:1px solid #000; padding:4px;">S.No</th>
            <th style="border:1px solid #000; padding:4px;">Name Of the Delegate</th>
            <th style="border:1px solid #000; padding:4px;">Secondary Company</th>
            <th style="border:1px solid #000; padding:4px;">Nationality</th>
            <th style="border:1px solid #000; padding:4px;">DOB</th>

            {# Dynamic day headers #}
            {% for d in dates %}
              <th style="border:1px solid #000; padding:4px; white-space:nowrap;">
                {{ frappe.utils.formatdate(d, "MMM dd") }}
              </th>
            {% endfor %}

            <th style="border:1px solid #000; padding:4px;">Result</th>
            <th style="border:1px solid #000; padding:4px;">Card Received</th>
          </tr>

          {# Serial counter via list length trick #}
          {% set serial = [] %}

          {# Rows for each student of this customer #}
          {% for k in doc.students if k.customer_name == customer %}
            {% set _ = serial.append(1) %}
            <tr>
              <td style="border:1px solid #000; padding:4px; text-align:center;">{{ serial|length }}</td>
              <td style="border:1px solid #000; padding:4px;">{{ k.student_name }}</td>


             {# Secondary Customer (replace old “Contact No.”) — value stored on Student #}
{% set secondary_customer = frappe.db.get_value("Student", k.student, "custom_secondary_customer") %}
<td style="border:1px solid #000; padding:4px;">
  {{ secondary_customer or "" }}
</td>

              {# Nationality #}
              {% set nationality = frappe.db.get_value("Student", k.student, "nationality") %}
              <td style="border:1px solid #000; padding:4px;">{{ nationality or "" }}</td>

              {# DOB #}
              {% set dob = frappe.db.get_value("Student", k.student, "date_of_birth") %}
              <td style="border:1px solid #000; padding:4px;">{{ frappe.utils.formatdate(dob) if dob else "" }}</td>

              {# Attendance cells for each date — now with signatures #}
                {% for d in dates %}
                  {% set att_name = frappe.db.get_value(
                    "Student Attendance",
                    {"date": d, "student_group": doc.name, "student": k.student, "docstatus": 1},
                    "name"
                  ) %}
                  {% set sig1 = None %}
                  {% set sig2 = None %}
                  {% if att_name %}
                    {% set sig1 = frappe.db.get_value("Student Attendance", att_name, "custom_student_signature1") %}
                    {% set sig2 = frappe.db.get_value("Student Attendance", att_name, "custom_student_signature") %}
                  {% endif %}
                  <td style="border:1px solid #000; padding:4px; text-align:center;">
                    {% if sig1 %}
                      <img src="{{ sig1 }}" style="width:60px; height:25px; object-fit:contain;" alt="Signature" />
                    {% elif sig2 %}
                      <img src="{{ sig2 }}" style="width:60px; height:25px; object-fit:contain;" alt="Signature" />
                    {% else %}
                      <!-- No signature -->
                    {% endif %}
                  </td>
                {% endfor %}


              {# Result (leave blank or fill as needed) #}
              <td style="border:1px solid #000; padding:4px;"></td>

              {# Card Received: show Student Card signature if exists #}
              {% set sig = None %}
              {% set s_card_name = frappe.db.get_value(
                "Student Card",
                {"student_group": doc.name, "student": k.student, "docstatus": 1},
                "name"
              ) %}
              {% if s_card_name %}
                {% set sig = frappe.db.get_value("Student Card", s_card_name, "student_signature") %}
              {% endif %}
              <td style="border:1px solid #000; padding:4px;">
                {% if sig %}
                  <img style="width:300px !important; height:70px !important; display:block;" src="{{ sig }}" alt="Signature" />
                {% else %}
                  No Signature
                {% endif %}
              </td>
            </tr>
          {% endfor %}
        </table>

        <div style="width:100%; overflow:auto;">
          <div style="float:left; margin-top:5%; width:50%;">
            {% if doc.instructors %}
              {% for i in doc.instructors %}
                <p>INSTRUCTOR'S NAME: <b>{{ i.instructor_name }}</b></p>
              {% endfor %}
            {% endif %}
            <p>Date:</p>
          </div>
          <div style="float:right; margin-top:5%; width:30%;">
            <p>SIGNATURE:</p>
          </div>
        </div>
      </div>
    {% endfor %}

  {% endif %}
</div>

<footer class="print-footer">
  <div class="col-md-12">
    <div class="row">
      <div id="footer-html" class="hidden-pdf">
        <div class="letter-head-footer">
          <table width="100%">
            <tr>
              <th>Po Box 132435, ICAD , Behind Dalma Mall Block B Mussaffah, Abu Dhabi , U.A.E</th>
            </tr>
          </table>
        </div>
      </div>
    </div>
  </div>
</footer>
