css:
.print-format {
    font-size: 12px;
    background: solid white;
}

.print-format .first-page-footer {
    margin-top: 100%;
    color:red;
}

.print-heading {
    border-bottom: 1px solid black;
    font-weight: 400;
    padding: 1px;
    margin-bottom: 2px;
    margin-top: 1px;
    color:black;
}

.print-heading h2 {
    font-size: 20px;
    }

.print-heading h2 div {
    font-weight: 400;
    padding-bottom: 1px;
    margin-bottom: 2px;
    margin-top: 2px;
    color: black;
}

.print-heading h2 br{
    display:none;
}

.print-heading small {
    font-size: 13px !important;
    font-weight: normal;
    line-height: 1;
    color: black;
    margin-top: 1px;
}

.print-format .letter-head {
    margin-bottom: 1px;
}

.print-format label {
    font-weight: 600;
    font-size: 12px;
     /*color: #821f17; */
    line-height: 1;
    margin-bottom: 0;
}

.print-format .data-field {
    margin-top: 0;
    margin-bottom: 0;
}

.print-format .value {
    color: black;
    line-height: 1.2;
}

.print-format .row:not(.section-break) {
    line-height: 1;
    margin-top: 6px !important;
    margin-bottom: 6px !important;
    color:black;
}

.print-format .important .value {
    font-size: 13px;
    font-weight: 600;
    
}

.print-format th {
    border:1px solid black;
     /*color: #e3e3e3; */
    /*background-color: #821f17;*/
    /* background-color: #808080; */
    text-transform: uppercase;
    font-weight: 600;
    font-size: 10px !important;
    text-align: center;
}

.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid black;
    
}

.print-format .table-bordered {
    vertical-align: middle !important;
    border: 1px solid black;

}

.print-format td, .print-format th {
    vertical-align: middle !important;
    font-size: 10px !important;
    padding: 2px !important;
}
.text-container {
    position: relative;
    height: 100%;
}

    .bottom-paragraph {
        position: fixed;
        bottom: 0;
        left: 0;
        right: 0;
        text-align: center;
        background-color: #f2f2f2;
        padding: 10px;
    }

.no-page-break{
    page-break-inside:avoid;
}

.page-break{
    page-break-before:always;
}
.page-number-container {
    position: absolute;
    bottom: 10px; 
    right: 10px; 
}
