html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto;">
{% else %}

<meta name="pdfkit-page-size" content="86mm x 54mm">
<meta name="pdfkit-orientation" content="Landscape">

<style>
@page { size: 86mm 54mm; margin: 0; }

.print-format { padding: 0 !important; margin: 0 !important; }

html, body {
  margin: 0 !important;
  padding: 0 !important;
  width: 86mm !important;
  height: 54mm !important;
  overflow: hidden !important;
  font-family: Arial, sans-serif;
}

/* CARD */
.card {
  width: 86mm;
  height: 54mm;
  padding: 3mm;
  border: 0.5mm solid #0046a6;
  border-radius: 5px;
  box-sizing: border-box;
  margin: 0 !important;
  overflow: hidden;
  position: relative;
}

/* TABLES */
.header-table, .body-table {
  width: 100%;
  border-collapse: collapse !important;
  border-spacing: 0 !important;
  margin: 0 !important;
}
.header-table td, .body-table td {
  padding: 0 !important;
  vertical-align: top !important;
}

/* HEADER */
.header-logo img { width: 8mm; height: 8mm; display: block; }

.header-title {
  font-size: 6pt;
  font-weight: 900;
  color: #114ad6;
  text-transform: uppercase;
  line-height: 1 !important;
  text-align: left;
}

.header-line td {
  border-bottom: 0.25mm solid #114ad6;
  height: 0 !important;
  line-height: 0 !important;
}

/* BODY TABLE spacing */
.body-table { margin-top: 0.8mm !important; }

/* CENTER TEXT CELL */
.center-cell {
  vertical-align: top;
  text-align: center;              /* ✅ center align whole block */
  padding: 0 !important;
}

/* TEXT BLOCK */
.center-block { margin-top: 0.4mm; }

.center-name {
  font-size: 7.6pt;                /* ✅ slightly bigger */
  font-weight: 800;
  margin: 0 0 0.4mm 0;
  text-transform: uppercase;
}
.center-subtext {
  font-size: 5pt;
  margin: 0 0 0.6mm 0;
}
.course-title {
  font-size: 5.2pt;
  font-weight: 900;
  margin: 0 0 0.4mm 0;
  text-transform: uppercase;
}
.course-desc {
  font-size: 5.2pt;
  margin: 0 0 0.5mm 0;
}

/* DETAILS lines */
.center-cell p {
  margin: 0.28mm 0;
  font-size: 5.3pt;               /* ✅ readable */
  line-height: 1.15;
}

/* QR */
.qr-cell {
  width: 16mm;
  text-align: right;
  vertical-align: top;
}
.qr-cell img {
  width: 16mm;
  height: 16mm;
  display: block;
}

/* FOOTER (BIGGER + FULL WIDTH) */
.footer {
  margin-top: 0.6mm;
  text-align: center;
}
.footer img {
  width: 100% !important;         /* ✅ full width */
  max-width: 100% !important;
  height: 9mm !important;         /* ✅ bigger footer (try 8.5mm-10mm) */
  object-fit: contain;
  display: block;
  margin: 0 auto;
}
</style>

<div class="card">

  <!-- HEADER -->
  <table class="header-table">
    <tr>
      <td class="header-logo">
        <img src="/files/Screenshot from 2025-07-15 08-45-36.png">
      </td>
      <td class="header-title">
        NUMERO UNO TRAINING &amp; CONSULTING LLC
      </td>
    </tr>
    <tr class="header-line">
      <td colspan="2"></td>
    </tr>
  </table>

  <!-- INSTRUCTOR -->
  {% set instructor = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
  {% if instructor %}
    {% set inst = frappe.get_doc("Instructor", instructor) %}
  {% endif %}

  {# 1) Student master image #}
  {% set student_image = frappe.db.get_value("Student", doc.student, "image") %}
  {# 2) Fallback from current doc #}
  {% set fallback_photo = doc.student_photo %}

  {# 3) Final #}
  {% if student_image %}
    {% set photo = student_image %}
  {% elif fallback_photo %}
    {% set photo = fallback_photo %}
  {% else %}
    {% set photo = None %}
  {% endif %}

  {# 4) Safe URL #}
  {% if photo %}
    {% if photo.startswith("http") %}
      {% set raw = photo %}
    {% else %}
      {% set raw = frappe.utils.get_url(photo) %}
    {% endif %}

    {% if "/files/" in raw %}
      {% set parts = raw.split("/files/", 1) %}
      {% set photo_url = parts[0] ~ "/files/" ~ (parts[1] | urlencode) %}
    {% else %}
      {% set photo_url = raw %}
    {% endif %}
  {% endif %}

  <!-- PHOTO OVERLAY -->
  {% if photo %}
    <div style="
      position:absolute;
      left:3mm;
      top:12.5mm;
      width:16mm;
      height:20mm;
      border:0.35mm solid #0046a6;
      box-sizing:border-box;
      overflow:hidden;
      background:#fff;
    ">
      <img src="{{ doc.student_photo }}" style="width:100%;height:100%;display:block;object-fit:cover;">
    </div>
  {% endif %}

  <!-- BODY (CENTER + QR) -->
  <table class="body-table">
    <tr>

      {% if photo %}
        <td class="center-cell" style="padding-left:19mm !important; padding-right:1mm !important;">
      {% else %}
        <td class="center-cell" style="padding-left:0 !important; padding-right:1mm !important;">
      {% endif %}

          <div class="center-block">

            <div class="center-name">{{ doc.student_name }}</div>
            <div class="center-subtext">has successfully completed the training</div>

            {% set course_doc = frappe.get_doc("Course", doc.course) %}

            <div class="course-title">
              {{ doc.course }}
              {% if doc.custom_level %} {{ doc.custom_level }}{% endif %}
            </div>

            {% if course_doc.description %}
              <div class="course-desc">{{ course_doc.description }}</div>
            {% endif %}

            <p><strong>No:</strong> {{ doc.name }}</p>
{% if doc.custom_include_company == 1 %}
            <p><strong>Customer:</strong> {{ frappe.db.get_value("Student", doc.student, "customer_name") }}</p>

{% endif %}
            {% if inst and inst.instructor_name %}
              <p><strong>Instructor:</strong> {{ inst.instructor_name }}</p>
            {% endif %}

            {% if doc.course_start_date %}
              <p><strong>Course Date:</strong> {{ frappe.utils.formatdate(doc.course_start_date) }} to {{ frappe.utils.formatdate(doc.course_end_date) }}</p>
            {% endif %}

            {% if doc.certificate_validity_date %}
              <p><strong>Expiry Date:</strong> {{ frappe.utils.formatdate(doc.certificate_validity_date) }}</p>
            {% endif %}


            {% if doc.custom_opito_learner_no %}
              <p><strong>Learner No:</strong> {{ doc.custom_opito_learner_no }}</p>
            {% endif %}



{% if doc.custom_unique_certificate_no %}
              <p><strong>Unique Certificate No:</strong> {{ doc.custom_unique_certificate_no }}</p>
            {% endif %}





          </div>
        </td>

      <td class="qr-cell">
        {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ doc.name %}
        <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150">
      </td>

    </tr>
  </table>

  <!-- FOOTER (BIGGER + FULL WIDTH) -->
  <div class="footer">
    <img src="/files/FOOTER.png">
  </div>

</div>

{% endif %}
