html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto;">
{% else %}
<style>
@page {
  size: 86mm 54mm;
  margin: 0;
}

/* Remove ERPNext padding */
.print-format {
  padding: 0 !important;
  margin: 0 !important;
}

html, body {
  margin: 0;
  padding: 0;
  font-family: Arial, sans-serif;
}

/* CARD WRAPPER */
.card {
  width: 86mm;
  min-height: 54mm;
  padding: 1mm 3mm 0mm 3mm;   /* bottom padding = 0 */
  border: 1px solid #1c3d7a;
  border-radius: 5px;
  box-sizing: border-box;

  display: flex;
  flex-direction: column;
}

/* CONTENT (Header + Body) */
.content {
  flex: 1;
  display: block;
}

/* HEADER + BODY TABLES */
.header-table,
.body-table {
  width: 100%;
  border-collapse: collapse;
  border-spacing: 0;
  margin: 0;
}

/* Header cells */
.header-table td {
  vertical-align: middle !important;
  padding: 0 !important;
  line-height: 1 !important;
}

/* Company name */
.header-title {
  font-size: 6pt;
  font-weight: 900;
  color: #114ad6;
  text-transform: uppercase;
  margin: 0 !important;
  padding: 0 !important;
  line-height: 1 !important;
}

/* Logo */
.header-logo img {
  width: 8mm;
  height: 8mm;
}

/* LINE under company name (works in PDF) */
.header-line td {
  border-bottom: 0.25mm solid #114ad6;
  padding: 0;
  margin: 0;
  height: 0;
  line-height: 0;
}

/* Body table spacing removed */
.body-table {
  margin-top: 0 !important;
}

/* PHOTO */
.photo-cell {
  width: 18mm;
  vertical-align: top;
}
.photo-box {
  width: 18mm;
  height: 22mm;
  border: 1px solid #114ad6;
}
.photo-box img {
  width: 100%;
  height: 100%;
}

/* CENTER TEXT */
.center-cell {
  width: 40mm;
  text-align: center;
  padding: 0 1mm;
  vertical-align: top;
}

.center-name {
  font-size: 7pt;
  font-weight: bold;
  margin: 0 0 0.3mm 0;
}

.center-subtext {
  font-size: 4.5pt;
  margin: 0 0 0.4mm 0;
}

.course-title {
  font-size: 7pt;
  font-weight: 900;
  text-transform: uppercase;
  margin: 0 0 0.3mm 0;
}

.course-desc {
  font-size: 5pt;
  margin: 0 0 0.6mm 0;
}

.center-cell p {
  margin: 0.2mm 0;
  font-size: 5pt;
}

/* QR */
.qr-cell {
  width: 18mm;
  text-align: right;
  vertical-align: top;
}
.qr-cell img {
  width: 18mm;
  height: 18mm;
}

/* FOOTER – no space below image */
.footer {
  margin-top: auto;
  text-align: center;
  padding: 0 !important;
  margin: 0 !important;
  line-height: 0 !important;
}
.footer img {
  width: 100%;
  height: auto;
  display: block;
  margin: 0 !important;
  padding: 0 !important;
}
</style>

<div class="card">

  <div class="content">

    <!-- HEADER -->
    <table class="header-table">
      <tr>
        <td class="header-logo">
          <img src="/files/Screenshot from 2025-07-15 08-45-36.png">
        </td>
        <td class="header-title">
          NUMERO UNO TRAINING &amp; CONSULTING LLC
        </td>
      </tr>
      <!-- LINE UNDER COMPANY NAME (instead of <hr>) -->
      <tr class="header-line">
        <td colspan="2"></td>
      </tr>
    </table>

    <!-- BODY -->
    {% set instructor = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
    {% if instructor %}
      {% set inst = frappe.get_doc("Instructor", instructor) %}
    {% endif %}

    <table class="body-table">
      <tr>

        <!-- PHOTO -->
        <td class="photo-cell">
          {% set photo = frappe.db.get_value("Student", doc.student, "custom_photo") %}
          {% if photo %}
            <div class="photo-box">
              <img src="{{ frappe.utils.get_url(photo) }}">
            </div>
          {% endif %}
        </td>

        <!-- CENTER TEXT -->
        <td class="center-cell">

          <div class="center-name">{{ doc.student_name }}</div>

          <div class="center-subtext">has successfully completed the training</div>

          {% set course_doc = frappe.get_doc("Course", doc.course) %}

          <div class="course-title">{{ doc.course }}</div>

          {% if course_doc.description %}
            <div class="course-desc">{{ course_doc.description }}</div>
          {% endif %}

          <p><strong>No:</strong> {{ doc.name }}</p>
          <p><strong>Customer:</strong> {{ frappe.db.get_value("Student", doc.student, "customer_name") }}</p>

          {% if inst and inst.instructor_name %}
            <p><strong>Instructor:</strong> {{ inst.instructor_name }}</p>
          {% endif %}

          {% if doc.custom_opito_learner_no %}
            <p><strong>Learner No:</strong> {{ doc.custom_opito_learner_no }}</p>
          {% endif %}

          {% if doc.custom_unique_certificate_no %}
            <p><strong>Certificate No:</strong> {{ doc.custom_unique_certificate_no }}</p>
          {% endif %}

          {% if doc.course_start_date %}
            <p><strong>Course Start Date:</strong> {{ frappe.utils.formatdate(doc.course_start_date) }}</p>
          {% endif %}

          {% if doc.certificate_validity_date %}
            <p><strong>Validity Date:</strong> {{ frappe.utils.formatdate(doc.certificate_validity_date) }}</p>
          {% endif %}
        </td>

        <!-- QR -->
        <td class="qr-cell">
          {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ doc.name %}
          <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150">
        </td>

      </tr>
    </table>

  </div>

  <!-- FOOTER IMAGE -->
  <div class="footer">
    <img src="/files/FOOTER.png">
  </div>

</div>

{% endif %}
