html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto;">
{% else %}
<style>
@page {
  size: 86mm 54mm;
  margin: 0;
}

/* Remove ERPNext padding */
.print-format {
  padding: 0 !important;
  margin: 0 !important;
}

html, body {
  margin: 0;
  padding: 0;
  font-family: Arial, sans-serif;
}

/* CARD WRAPPER */
.card {
  width: 80mm;                 /* card size */
  min-height: 48mm;            /* approx height, but auto expand if needed */
  padding: 3mm 3mm 3mm 3mm;
  border: 0.5mm solid #0046a6; /* darker + thicker border */
  border-radius: 5px;
  box-sizing: border-box;
  margin: 2mm auto 0 auto;
}

/* CONTENT (Header + Body) */
.content {
  display: block;
}

/* HEADER + BODY TABLES */
.header-table,
.body-table {
  width: 100%;
  border-collapse: collapse;
  border-spacing: 0;
  margin: 0;
}

/* Header cells */
.header-table td {
  vertical-align: middle !important;
  padding: 0 !important;
  line-height: 1 !important;
}

/* Company name */
.header-title {
  font-size: 6pt;
  font-weight: 900;
  color: #114ad6;
  text-transform: uppercase;
  margin: 0 !important;
  padding: 0 !important;
  line-height: 1 !important;
}

/* Logo */
.header-logo img {
  width: 8mm;
  height: 8mm;
}

/* LINE under company name */
.header-line td {
  border-bottom: 0.25mm solid #114ad6;
  padding: 0;
  margin: 0;
  height: 0;
  line-height: 0;
}

/* Body table – small gap */
.body-table {
  margin-top: 1mm !important;
}

/* PHOTO CELL – BORDER NOW ON TD ITSELF */
.photo-cell {
  width: 16mm;
  vertical-align: top;
  padding: 0 !important;
  border: 0.35mm solid #0046a6;   /* 4-side visible box */
}

/* INNER BOX ONLY FOR PADDING */
.photo-box {
  width: 100%;
  height: 20mm;
  box-sizing: border-box;
}

/* Image inset so border fully visible */
.photo-box img {
  width: calc(100% - 1mm);
  height: calc(100% - 1mm);
  margin: 0.5mm;
  display: block;
}

/* CENTER TEXT CELL */
.center-cell {
  width: 38mm;
  text-align: center;
  padding: 0 1mm;
  vertical-align: top;
}

/* When NO photo, text left aligned and wider */
.center-cell.no-photo {
  text-align: left;
  padding-left: 1mm;
}

/* CENTER BLOCK - moved down */
.center-block {
  margin-top: 3.2mm;
}

.center-name {
  font-size: 7pt;
  font-weight: bold;
  margin: 0 0 0.3mm 0;
}

.center-subtext {
  font-size: 4.5pt;
  margin: 0 0 0.4mm 0;
}

.course-title {
  font-size: 7pt;
  font-weight: 900;
  text-transform: uppercase;
  margin: 0 0 0.3mm 0;
}

.course-desc {
  font-size: 5pt;
  margin: 0 0 0.6mm 0;
}

.center-cell p {
  margin: 0.25mm 0;
  font-size: 5pt;
}

/* QR CELL */
.qr-cell {
  width: 18mm;
  text-align: right;
  vertical-align: top;
}
.qr-cell img {
  width: 18mm;
  height: 18mm;
}

/* FOOTER – normal flow */
.footer {
  margin-top: 2mm;
  text-align: center;
}
.footer img {
  width: 100%;
  height: auto;
  display: block;
  margin: 0 !important;
  padding: 0 !important;
}
</style>

<div class="card">

  <div class="content">

    <!-- HEADER -->
    <table class="header-table">
      <tr>
        <td class="header-logo">
          <img src="/files/Screenshot from 2025-07-15 08-45-36.png">
        </td>
        <td class="header-title">
          NUMERO UNO TRAINING &amp; CONSULTING LLC
        </td>
      </tr>

      <!-- LINE UNDER COMPANY NAME -->
      <tr class="header-line">
        <td colspan="2"></td>
      </tr>
    </table>

    <!-- BODY -->
    {% set instructor = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
    {% if instructor %}
      {% set inst = frappe.get_doc("Instructor", instructor) %}
    {% endif %}

    <table class="body-table">
      <tr>

        {% set photo = frappe.db.get_value("Student", doc.student, "custom_photo") %}

        {# ------------ CASE 1: PHOTO AVAILABLE ------------ #}
        {% if photo %}
          <!-- PHOTO CELL -->
          <td class="photo-cell">
            <div class="photo-box">
              <img src="{{ frappe.utils.get_url(photo) }}">
            </div>
          </td>

          <!-- CENTER TEXT (centered) -->
          <td class="center-cell">
            <div class="center-block">

              <div class="center-name">{{ doc.student_name }}</div>

              <div class="center-subtext">
                has successfully completed the training
              </div>

              {% set course_doc = frappe.get_doc("Course", doc.course) %}

              <div class="course-title">
                {{ doc.course }}
              </div>

              {% if course_doc.description %}
                <div class="course-desc">
                  {{ course_doc.description }}
                </div>
              {% endif %}

              <p><strong>No:</strong> {{ doc.name }}</p>
              <p><strong>Customer:</strong> {{ frappe.db.get_value("Student", doc.student, "customer_name") }}</p>

              {% if inst and inst.instructor_name %}
                <p><strong>Instructor:</strong> {{ inst.instructor_name }}</p>
              {% endif %}

              {% if doc.course_start_date %}
                <p><strong>Course Start Date:</strong> {{ frappe.utils.formatdate(doc.course_start_date) }}</p>
              {% endif %}

              {% if doc.certificate_validity_date %}
                <p><strong>Validity Date:</strong> {{ frappe.utils.formatdate(doc.certificate_validity_date) }}</p>
              {% endif %}

            </div>
          </td>

        {# ------------ CASE 2: NO PHOTO ------------ #}
        {% else %}

          <!-- CENTER TEXT spanning photo + text area, left aligned -->
          <td class="center-cell no-photo" colspan="2">
            <div class="center-block">

              <div class="center-name">{{ doc.student_name }}</div>

              <div class="center-subtext">
                has successfully completed the training
              </div>

              {% set course_doc = frappe.get_doc("Course", doc.course) %}

              <div class="course-title">
                {{ doc.course }}
              </div>

              {% if course_doc.description %}
                <div class="course-desc">
                  {{ course_doc.description }}
                </div>
              {% endif %}

              <p><strong>No:</strong> {{ doc.name }}</p>
              <p><strong>Customer:</strong> {{ frappe.db.get_value("Student", doc.student, "customer_name") }}</p>

              {% if inst and inst.instructor_name %}
                <p><strong>Instructor:</strong> {{ inst.instructor_name }}</p>
              {% endif %}

              {% if doc.course_start_date %}
                <p><strong>Course Start Date:</strong> {{ frappe.utils.formatdate(doc.course_start_date) }}</p>
              {% endif %}

              {% if doc.certificate_validity_date %}
                <p><strong>Validity Date:</strong> {{ frappe.utils.formatdate(doc.certificate_validity_date) }}</p>
              {% endif %}

            </div>
          </td>

        {% endif %}

        <!-- QR (always on right) -->
        <td class="qr-cell">
          {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ doc.name %}
          <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150">
        </td>

      </tr>
    </table>

  </div>

  <!-- FOOTER IMAGE -->
  <div class="footer">
    <img src="/files/FOOTER.png">
  </div>

</div>

{% endif %}
