html:
{% if doc.custom_certificate %}
  <img src="{{ doc.custom_certificate }}" style="width:100%; height:auto;">
{% else %}
<style>
@page { size: 86mm 54mm; margin: 0; }

html, body {
  margin:0; padding:0;
  font-family: Arial, sans-serif;
}

/* Outer Card */
.card {
  width: 86mm;
  height: 54mm;
  padding: 2mm 3mm;
  border: 1px solid #1c3d7a;
  border-radius: 5px;
  box-sizing: border-box;
}

/* Header */
.header {
  display: flex;
  align-items: center;
  gap: 2mm;
  border-bottom: 0.2mm solid #ccc;
  padding-bottom: 1mm;
}

.header img {
  width: 8mm;
  height: 8mm;
  object-fit: contain;
}

.header h2 {
  margin: 0;
  font-size: 6pt;
  font-weight: 900;
  color: #114ad6;
  text-transform: uppercase;
}

/* Body */
.body {
  display: flex;
  justify-content: space-between;
  margin-top: 1mm;
}

/* Photo */
.photo {
  width: 18mm;
  height: 24mm;
  border: 1px solid #114ad6;
}

.photo img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Center Text */
.center {
  width: 40mm;
  text-align: center;
  padding: 0 1mm;
}

.center .name {
  font-size: 7pt;
  font-weight: bold;
}

.center .course {
  font-size: 5.5pt;
  margin: 1mm 0;
}

.center p {
  margin: 0.5mm 0;
  font-size: 5pt;
}

/* QR */
.qr img {
  width: 18mm;
  height: 18mm;
}

/* Signature */
.signature-block {
  margin-top: 1mm;
  display: flex;
  align-items: flex-start;
  gap: 2mm;
}

.signature-block img {
  width: 16mm;
  height: 8mm;
  object-fit: contain;
}

.signature-info {
  font-size: 5pt;
  line-height: 1.2;
  margin-top: 2mm;
}

.line {
  border-top: 0.2mm solid #333;
  width: 30mm;
  margin-top: 1mm;
}

/* Footer */
.footer {
  margin-top: 1mm;
}

.footer img {
  width: 100%;
  height: auto;
}
</style>

<div class="card">

  <!-- Header -->
  <div class="header">
    <img src="/files/Screenshot from 2025-07-15 08-45-36.png">
    <h2>NUMERO UNO TRAINING & CONSULTING LLC</h2>
  </div>

  <!-- Body -->
  <div class="body">

    <!-- Photo -->
    <div>
      {% if frappe.db.get_value("Student", doc.student, "custom_photo") %}
      <div class="photo">
        <img src="{{ frappe.utils.get_url(frappe.db.get_value('Student', doc.student, 'custom_photo')) }}">
      </div>
      {% endif %}
    </div>


    {% set instructor = frappe.db.get_value("Student Group Instructor", {"parent": doc.student_group}, "instructor") %}
    {% if instructor %}
      {% set inst = frappe.get_doc("Instructor", instructor) %}

    <!-- Center Section -->
    <div class="center">
      <div class="name">{{ doc.student_name }}</div>
      <div class="course">{{ doc.course }}</div>

      <p><strong>No:</strong> {{ doc.name }}</p>
      <p><strong>Customer:</strong> {{ frappe.db.get_value("Student", doc.student, "customer_name") }}</p>


     {% if inst and inst.instructor_name and inst.instructor_name.strip() %}
<p><strong>Instructor:</strong> {{ inst.instructor_name }}</p>
{% endif %}

{% if doc.custom_opito_learner_no and doc.custom_opito_learner_no.strip() %}
<p><strong>Learner No:</strong> {{ doc.custom_opito_learner_no }}</p>
{% endif %}

{% if doc.custom_unique_certificate_no and doc.custom_unique_certificate_no.strip() %}
<p><strong>Certificate No:</strong> {{ doc.custom_unique_certificate_no }}</p>
{% endif %}


    </div>

     {% endif %}


    <!-- QR Code -->
    <div class="qr">
      {% set verify_url = frappe.utils.get_url() ~ "/certificate-verification?cert=" ~ doc.name %}
      <img src="https://barcode.tec-it.com/barcode.ashx?data={{ verify_url|urlencode }}&code=QRCode&dpi=150">
    </div>

  </div>

  <!-- Signature -->


  <!-- Footer Icons -->
  <div class="footer">
    <img src="/files/FOOTER.png">
  </div>

</div>

{% endif %}
